DECLARE @Subject nvarchar(MAX) = N'Database Mail Test ' + @@SERVERNAME;
DECLARE @Body nvarchar(MAX) = N'This is a test e-mail sent from Database Mail on ' + @@SERVERNAME + N'.';

EXEC dbo.sp_send_dbmail
    @profile_name = 'SQLAdmin'
   ,@recipients = N'kevin.martin@venturesolutions.com'
   ,@subject = @Subject
   ,@body = @Body;
